    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        ArrayList login = new ArrayList();
        String pass=String.copyValueOf(jPasswordField1.getPassword());
        String user=jComboBox1.getSelectedItem().toString();
        
        switch (user) {
            case "Admin":
                login.add(0,"admin");
                break;
            case "Player":
                login.add(0,"player");
                break;
            case "Coach":
                login.add(0,"coach");
                break;
            case "Event Manager":
                login.add(0,"event_mgr");
                break;
            case "Physio":
                login.add(0,"physio");
                break;
            default:
                break;
        }
        if(jTextField1.getText().length()==0){
            JOptionPane.showMessageDialog(rootPane, "Plese Enter user ID.");
            return;
        }
        else if(pass.length()==0){
            JOptionPane.showMessageDialog(rootPane, "Plese Enter password.");
            return;
        }
        else{
            login.add(1,jTextField1.getText());
            login.add(2,pass);
            if(login.get(0).equals("admin")&&login.get(1).equals("hanif")&&login.get(2).equals("12345")){
                AdminFrame ad=new AdminFrame();
                ad.setVisible(true);
                this.dispose();
            }
            else if(login.get(0).equals("admin")&&(!login.get(1).equals("hanif") || !login.get(2).equals("12345"))){
                JOptionPane.showMessageDialog(null, "User ID or Password is incorrect!");
            }
            else if(!login.get(0).equals("admin")){
            DBConnect dbc = new DBConnect();
            dbc.login(login);
            if(dbc.moveFrame(login)){
                this.dispose();
            }
            }
            
        }
    }//GEN-LAST:event_jButton1ActionPerformed

